function setHTMLContentOfENQuote(t){document.getElementById("quote-content").innerText=t.content,document.getElementById("quote-mean").innerText=t.mean,document.getElementById("quote-link").innerText=t.link}function setHTMLContentOfJPQuote(t){document.getElementById("quote-content").innerText=t.content}function displayQuoteOf(n){let t="";switch(n){case"EN":t="https://tuananhhedspibk.github.io/blog/data/quotes-en.json";break;case"JP":default:t="https://tuananhhedspibk.github.io/blog/data/quotes-jp.json"}let o=new XMLHttpRequest;o.open("GET",t,!1),o.onreadystatechange=function(){if(4===o.readyState&&(200===o.status||0==o.status)){var t=JSON.parse(o.responseText),e=Math.floor(Math.random()*t.quotes.length);switch(n){case"EN":setHTMLContentOfENQuote(t.quotes[e]);break;case"JP":default:setHTMLContentOfJPQuote(t.quotes[e])}}},o.send(null)}